function Variable(){
    var _this=this;
    this.put=function(key,val){
        if(key.indexOf('.')!=-1){
            var m=key.split('.');
            var kn='';
            m.forEach(function(v,k){
                kn+='.'+v;
                var mal=eval("_this"+kn);
                if(!mal){
                    eval("_this"+kn+"={}");
                }
                if(k==(m.length-1)){
                    eval("_this"+kn+"=val");
                }
            });
        }else{
            _this[key]=val;
        }
    };
    this.get=function(key){
        if(key.indexOf('.')!=-1){
            var r=eval("_this."+key);
            if(r){
                return r;
            }else{
                return undefined;
            }
        }else{
            var r=_this[key];
            if(r){
                return r;
            }else{
                return undefined;
            }
        }
    };
}
module.exports=Variable;